      font-size: 13px;
      font-weight: 600;
      width: 46px;
      line-height: 24px;
      border-radius: 23px;
      text-align: center;
      background-color: #e9f0ff;
      margin-bottom: 26px;
    }

    &__description {
      font-family: "Open Sans", Helvetica, sans-serif;
      font-size: 13px;
      line-height: 19px;
      font-weight: 600;
      color: #6c788b;
      margin-bottom: 42px;
    }

    &__link {
      position: relative;
      line-height: 40px;
      padding-left: 56px;
      text-decoration: none;
      color: #334563;
      font-weight: 600;
      font-size: 15px;

      &::before {
        content: "";
        display: block;
        position: absolute;
        left: 0;
        width: 40px;
        height: 40px;
        background-color: #2060f6;
        background-image: url(../images/vector-images/right-arrow.svg);
        background-repeat: no-repeat;
        background-position: center;
        border-radius: 50%;
      }

      @include transition;
      &:hover {
        font-size: 16px;
      }

      &:hover::before {
        @keyframes move-both {
          from {
            left: 0;
          }
          to {
            left: 10px;
          }
        }

        animation: 0.4s
          cubic-bezier(0.55, 0.09, 0.68, 0.53)
          0s
          infinite
          alternate
          both
          running
          move-both;
      }
    }
  }

}

