.footer {
  &__navigation {
    position: static;
    height: 300px;

    @include onTablet {
      height: 100px;
    }
  }

  .short-navigation {
    position: static;
    pointer-events: all;
    top: 0;
    align-items: start;
    flex-direction: column;
    gap: 24px;
  }

  .navigation__list {
    position: static;
    justify-content: space-between;
    width: 100%;

    @include onTablet {
      flex-direction: row;
    }
  }

  .navigation__link {
    font-size: 13px;
  }
}
